mapper.select:
select count(1) from t_wb_order LEFT JOIN t_wb_user on t_wb_user.id = t_wb_order.user_id

    where t_wb_order.merchant_id in

        #{ages}



    and t_wb_user.real_name like CONCAT('%',#{userName},'%')


    and t_wb_order.merchant_id = #{merchantId}


    and t_wb_order.pay_time like CONCAT(#{dateOfTransaction},'%')

select t_wb_order.* from t_wb_order LEFT JOIN t_wb_user on t_wb_user.id = t_wb_order.user_id

    where t_wb_order.merchant_id in

        #{ages}



    and t_wb_user.real_name like CONCAT('%',#{userName},'%')


    and t_wb_order.merchant_id = #{merchantId}


    and t_wb_order.pay_time like CONCAT(#{dateOfTransaction},'%')

limit #{current},#{size}

SELECT t_wb_order.*, SUM(t_wb_order_bill.total_fee) as total_fee,t_wb_order_bill.type, t_wb_user.mobile AS mobile FROM t_wb_order LEFT JOIN t_wb_user ON t_wb_order.user_id = t_wb_user.id LEFT JOIN t_wb_order_bill ON t_wb_order.order_id= t_wb_order_bill.order_id WHERE t_wb_order.status >= 1
and t_wb_order.merchant_id = #{merchantId}
and t_wb_order.pay_time like CONCAT(#{dateOfTransaction},'%')
and t_wb_user.real_name like CONCAT('%',#{userName},'%')
GROUP BY t_wb_order.order_id limit #{current},#{size}
