mapper.select:
select t_wb_order.*,t_wb_user.*,(SELECT total_fee FROM t_wb_order_bill WHERE t_wb_order_bill.order_id = t_wb_order.order_id AND t_wb_order_bill.type = 1 ) AS total_fee, (SELECT total_fee FROM t_wb_order_bill WHERE t_wb_order_bill.order_id = t_wb_order.order_id AND t_wb_order_bill.type = 2 ) AS coupons_offset from t_wb_order LEFT JOIN t_wb_user on t_wb_user.id = t_wb_order.user_id LEFT JOIN t_merchant on t_merchant.id = t_wb_order.merchant_id
where t_wb_order.merchant_id in
#{ages}
and (t_wb_user.real_name like CONCAT('%',#{userName},'%') or t_wb_user.mobile like CONCAT('%',#{userName},'%'))
and t_wb_order.order_id like CONCAT('%',#{orderId},'%')
and t_merchant.merchant_name like CONCAT('%',#{merchantName},'%')
AND t_wb_order.create_time BETWEEN #{startTime} AND #{endTime}
limit #{current},#{size}

SELECT t_wb_order.*, ( SELECT total_fee FROM t_wb_order_bill WHERE t_wb_order_bill.order_id = t_wb_order.order_id AND t_wb_order_bill.type = 1 ) AS total_fee, t_wb_user.mobile AS mobile from t_wb_order LEFT JOIN t_wb_user ON t_wb_user.id = t_wb_order.user_id WHERE t_wb_order.status >= 1
and t_wb_order.merchant_id = #{merchantId}
and t_wb_order.pay_time like CONCAT(#{dateOfTransaction},'%')
and t_wb_user.real_name like CONCAT('%',#{userName},'%')
limit #{current},#{size}
