mapper.select:
SELECT t_sys_user.user_name AS sysUsetName,COUNT(1) AS numBers
FROM t_merchant INNER JOIN t_sys_user ON t_merchant.sys_user_id = t_sys_user.user_id WHERE 1=1

    AND t_merchant.province = #{province}


    AND t_merchant.city = #{city}


    AND t_merchant.area = #{area}


    AND t_merchant.town = #{town}


    AND t_sys_user.user_name LIKE CONCAT('%',#{sysUserName},'%')


    AND t_merchant.create_time BETWEEN #{startTime} AND #{endTime}

GROUP BY t_merchant.sys_user_id
